import moment from 'moment'


const getUpcomingForecast = (list) => {
    list.slice(1,6).map((item) => ({
        day: moment(item.time * 1000).format('dddd').substring(0, 3),
        icon: item.weather[0].icon,
        temp: item.temp.day
    }))
}

export default getUpcomingForecast
